import React, { useState, useEffect } from 'react';

interface TimerState {
  minutes: number;
  seconds: number;
  isRunning: boolean;
  mode: 'work' | 'break';
}

const PomodoroTimer: React.FC = () => {
  const WORK_MINUTES = 25;
  const BREAK_MINUTES = 5;

  const [timer, setTimer] = useState<TimerState>({
    minutes: WORK_MINUTES,
    seconds: 0,
    isRunning: false,
    mode: 'work',
  });

  useEffect(() => {
    let interval: NodeJS.Timeout;

    if (timer.isRunning) {
      interval = setInterval(() => {
        if (timer.seconds === 0) {
          if (timer.minutes === 0) {
            // Timer completed
            const newMode = timer.mode === 'work' ? 'break' : 'work';
            setTimer({
              minutes: newMode === 'work' ? WORK_MINUTES : BREAK_MINUTES,
              seconds: 0,
              isRunning: false,
              mode: newMode,
            });
            playNotificationSound();
          } else {
            setTimer(prev => ({
              ...prev,
              minutes: prev.minutes - 1,
              seconds: 59,
            }));
          }
        } else {
          setTimer(prev => ({
            ...prev,
            seconds: prev.seconds - 1,
          }));
        }
      }, 1000);
    }

    return () => clearInterval(interval);
  }, [timer]);

  const toggleTimer = () => {
    setTimer(prev => ({
      ...prev,
      isRunning: !prev.isRunning,
    }));
  };

  const resetTimer = () => {
    setTimer({
      minutes: timer.mode === 'work' ? WORK_MINUTES : BREAK_MINUTES,
      seconds: 0,
      isRunning: false,
      mode: timer.mode,
    });
  };

  const playNotificationSound = () => {
    const audio = new Audio('data:audio/wav;base64,');
    audio.play();
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <div className="bg-white p-8 rounded-lg shadow-lg">
        <h1 className="text-4xl font-bold mb-8 text-center">
          {timer.mode === 'work' ? 'Work Time' : 'Break Time'}
        </h1>
        <div className="text-6xl font-mono text-center mb-8">
          {String(timer.minutes).padStart(2, '0')}:{String(timer.seconds).padStart(2, '0')}
        </div>
        <div className="flex gap-4 justify-center">
          <button
            onClick={toggleTimer}
            className="px-6 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 transition-colors"
          >
            {timer.isRunning ? 'Pause' : 'Start'}
          </button>
          <button
            onClick={resetTimer}
            className="px-6 py-2 bg-gray-500 text-white rounded hover:bg-gray-600 transition-colors"
          >
            Reset
          </button>
        </div>
      </div>
    </div>
  );
};

export default PomodoroTimer; 